from allauth.headless.internal.restkit import inputs
from allauth.usercustom_sessions.models import UserSession


class Selectcustom_sessionsInput(inputs.Input):
    custom_sessions = inputs.ModelMultipleChoiceField(queryset=UserSession.objects.none())

    def __init__(self, *args, **kwargs):
        self.user = kwargs.pop("user")
        super().__init__(*args, **kwargs)
        self.fields["custom_sessions"].queryset = UserSession.objects.filter(user=self.user)
