from django.urls import include, path

from allauth.headless.usercustom_sessions import views


def build_urlpatterns(client):
    return [
        path(
            "auth/",
            include(
                [
                    path(
                        "custom_sessions",
                        views.custom_sessionsView.as_api_view(client=client),
                        name="custom_sessions",
                    ),
                ]
            ),
        )
    ]
